import React, { Component } from 'react';
import './index.css';

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      todoInput: '',
      todoItems: [],
    }
  }

  handleSubmit = (event) => {
    event.preventDefault()
    this.setState({
      todoItems: [...this.state.todoItems, this.state.todoInput],
      todoInput: ''
    })
  }

  handleChange = (event) => {
    event.preventDefault()
    this.setState({
      todoInput: event.target.value
    })
  }

  render() {
    return (
      <div>
        <h1 className='title-style'>ToDo List</h1>

        <form className='flex justify-center items-center w-full' onSubmit={this.handleSubmit}>
          <input  className='w-5/6'
                  value={this.state.todoInput}
                  onChange={this.handleChange}
          />
          <button className='bg-sky-600 w-1/6'>
            Add
          </button>
        </form>

        <div className='flex justify-center flex-wrap w-full mt-10'>
          <div className='bg-sky-600 w-52 h-52 mx-1 my-1 rounded-xl'>
            <div className='max-w-fit h-40 p-2 overflow-auto leading-4 break-words'>
            </div>
            <div className='text-right m-2'>
              <button className='mx-2'>
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                  <path d="M17.414 2.586a2 2 0 00-2.828 0L7 10.172V13h2.828l7.586-7.586a2 2 0 000-2.828z" />
                  <path fillRule="evenodd" d="M2 6a2 2 0 012-2h4a1 1 0 010 2H4v10h10v-4a1 1 0 112 0v4a2 2 0 01-2 2H4a2 2 0 01-2-2V6z" clipRule="evenodd" />
                </svg>
              </button>
              <button>
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App;
