import React, { Component } from 'react';
import List from './components/List'
import './index.css';

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      todoInput: '',
      todoItems: [],
    }
  }

  handleSubmit = (event) => {
    event.preventDefault()
    this.setState({
      todoItems: [...this.state.todoItems, this.state.todoInput],
      todoInput: ''
    })
  }

  handleChange = (event) => {
    event.preventDefault()
    this.setState({
      todoInput: event.target.value
    })
  }

  render() {
    return (
      <div>
        <h1 className='title-style'>ToDo List</h1>

        <form className='flex justify-center items-center w-full' onSubmit={this.handleSubmit}>
          <input  className='w-5/6'
                  value={this.state.todoInput}
                  onChange={this.state.handleChange}
          />
          <button className='bg-sky-600 w-1/6'>
            Add
          </button>
        </form>

        <List 
          todolist={this.state.todoItems}
        />

      </div>
    )
  }
}

export default App;
